import React from 'react';
import './App.css';
import { Switch, Route} from 'react-router-dom';
import Header from './components/header/header.component'
import Homepage from './pages/homepage/homepage.component'
import AuthPage from './pages/authPage/authPage.component'
import GymList from './pages/gymList/gymList.component'
import Logout from './components/logout/logout.component'

class App extends React.Component {

  constructor(){
    super()
    this.state = {
      url: "https://web-gym2.herokuapp.com/",
      loggedIn: false,
      username: '',
      name: "",
      surname: "",
      coach: false,
      admin: false,
      gymOwner: false
    }
  }

  handleLogin = (username, name, surname, coach, admin, gymOwner) => {
    this.setState({
      name:name,
      surname: surname,
      coach: coach,
      admin:admin,
      gymOwner: gymOwner,
      username: username,
      loggedIn: true
    })
  }

  handleLogout = () => {
    this.setState({
      name:'',
      surname: '',
      coach: '',
      admin:false,
      gymOwner: false,
      username: false,
      loggedIn: false
    })
  }

  render(){
    return (
      <div className="App">
          <Header loggedIn = {this.state.loggedIn}/>
          <div className = 'page-container'>
            <Switch>
              <Route exact path="/" component={Homepage} />
              <Route exact path="/auth" render={(props) => <AuthPage {...props} handleLogin={this.handleLogin} />} />
              <Route exact path="/logout" render={(props) => <Logout {...props} handleLogout={this.handleLogout} />} />
              <Route exact path="/gymList" component={GymList} />
            </Switch>
          </div>
      </div>
    )
  }
}

export default App;
