import React from 'react';

import './formInput.styles.scss';

const FormInput = ({label, handleChange, ...otherProps }) => (
  <div className='group'>
  <input className={`${otherProps.short ? 'short' : ''} 'form-input'`} onChange={handleChange} {...otherProps} />
  {label ? (
    <label
      className={`${
        otherProps.value.length ? 'shrink' : ''
      } form-input-label`}
    >
      {label}
    </label>
  ) : null}
</div>
);

export default FormInput;
