import React from 'react'
import './authPage.styles.scss'
import CustomButton from '../../components/custom-buttom/custom-button.component'
import FormInput from '../../components/formInput/formInput.component'

class AuthPage extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      username: '',
      password: '',
      name:"",
      surname: "",
      email: "",
      error: ''
    }
  }

  handleChange = event => {
    const { value, name } = event.target;

    this.setState({ [name]: value });
  };

  handleLogin = async (name, surname, coach, admin, gymOwner) => {
      await this.props.handleLogin(name, surname, coach, admin, gymOwner)
      this.props.history.push("/")
  }


  handleButtonLoginClick = () => {
    fetch('https://6841124a-8115-4b62-bdf7-5dcdbd3c17a9.mock.pstmn.io/login', {
      method: 'GET',
      credentials: 'include',
      body: JSON.stringify({username:this.state.username, password:this.state.password})
    }).then(response => {
      if(response.status === 200){
        return response.json()
      } else {
        return Promise.reject()
      }
    }).then(res => {
      console.log(res)
      this.handleLogin(res.name, res.surname, res.coach, res.admin, res.gymOwner)
    }, function(){
      alert("Krivo korisnicko ime ili lozinka")
    })
  }

  handleButtonRegisterClick = () => {
    fetch('https://6841124a-8115-4b62-bdf7-5dcdbd3c17a9.mock.pstmn.io/registration', {
      method: 'GET'
      //credentials: 'include',
      //body: JSON.stringify({username:this.state.username, password:this.state.password})
    }).then(response => {

    })
  }


  render() {
    return (
      <div className='auth-page'>
        <fieldset>
          <legend>Prijava</legend>
          <div className='login-container'>
            <FormInput
                name='username'
                type='username'
                handleChange={this.handleChange}
                value={this.state.username}
                label='Korisničko ime'
                required
            />
            <div className='padding-bottom'>
              <FormInput
                  name='password'
                  type='password'
                  value={this.state.password}
                  handleChange={this.handleChange}
                  label='Lozinka'
                  required
              />
            </div>
            <CustomButton onClick = {this.handleButtonLoginClick} > Prijava </CustomButton>
            <p>{this.state.error}</p>
          </div>
        </fieldset>

        <fieldset>
          <legend>Registracija</legend>
          <div className='registration-container'>
            <div className = 'registration-name-and-surname'>
              <FormInput
                  name='name'
                  type='name'
                  handleChange={this.handleChange}
                  value={this.state.name}
                  label='Ime'
                  required
                  style = {{"width": "130px"}}
              />
              <FormInput
                  name='surname'
                  type='surname'
                  handleChange={this.handleChange}
                  value={this.state.surname}
                  label='Prezime'
                  required
                  style = {{"width": "130px"}}
              />
            </div>
            <FormInput
                name='email'
                type='email'
                handleChange={this.handleChange}
                value={this.state.email}
                label='E-posta'
                required
            />
            <div className= 'username-and-password-registration'>
              <FormInput
                  name='username'
                  type='username'
                  handleChange={this.handleChange}
                  value={this.state.username}
                  label='Korisničko ime'
                  required
              />
              <FormInput
                  name='password'
                  type='password'
                  value={this.state.password}
                  handleChange={this.handleChange}
                  label='Lozinka'
                  required
              />
            </div>
            <CustomButton onClick = {this.handleButtonRegisterClick}> Registracija </CustomButton>
            <p>{this.state.error}</p>
          </div>
        </fieldset>
      </div>
    );
  }
}

export default AuthPage
