import React from 'react'
import './authPage.styles.scss'
import CustomButton from '../../components/custom-buttom/custom-button.component'
import FormInput from '../../components/formInput/formInput.component'

class AuthPage extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      username: '',
      password: '',
      error: ''
    }
  }

  handleChange = event => {
    const { value, name } = event.target;

    this.setState({ [name]: value });
  };

  handleLogin = async (name, surname, coach, admin, gymOwner) => {
      await this.props.handleLogin(name, surname, coach, admin, gymOwner)
      this.props.history.push("/")
  }


  handleSubmit = () => {
    fetch('http://localhost:5000/login', {
      method: 'POST',
      credentials: 'include',
      body: JSON.stringify({username:this.state.username, password:this.state.password})
    }).then(response => {
      if(response.status === 200){
        return response.json()
      } else {
        return reject
      }
    }).then(res => {
      this.handleLogin(res.name, res.surname, res.coach, res.admin, res.gymOwner)
    }, () => {
      alert("Krivo korisnicko ime ili lozinka")
    })
  }



  render() {
    return (
      <div className='login-page'>
        <fieldset>
          <legend>Prijava</legend>
          <div className='login-container'>
            <form onSubmit={this.handleSubmit}>
              <FormInput
                  name='username'
                  type='username'
                  handleChange={this.handleChange}
                  value={this.state.username}
                  label='Korisničko ime'
                  required
              />
              <FormInput
                  name='password'
                  type='password'
                  value={this.state.password}
                  handleChange={this.handleChange}
                  label='Lozinka'
                  required
              />
              <CustomButton type='submit'> Prijava </CustomButton>
              <p>{this.state.error}</p>
            </form>
          </div>
        </fieldset>
      </div>
    );
  }
}

export default AuthPage
