import React from 'react'
import './authPage.styles.scss'
import CustomButton from '../../components/custom-buttom/custom-button.component'
import FormInput from '../../components/formInput/formInput.component'

class AuthPage extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      username: '',
      password: '',
      error: ''
    }
  }

  handleChange = event => {
    const { value, name } = event.target;

    this.setState({ [name]: value });
  };

  handleLogin = async (ok, user, username, manager, admin, superadmin, FullName) => {
    if(ok){
      await this.props.handleLogin(user, username, manager, admin, superadmin, FullName)
      this.props.history.push("/")
    } else {
      this.setState({error:"Incorrect username or password"})
    }
  }

  handleSubmit = (event) => {
    fetch('http://localhost:5000/login', {
      method: 'POST',
      credentials: 'include',
      body: JSON.stringify({username:this.state.username, password:this.state.password})
    }).then(response => response.json())
        .then(res => {
          console.log()
          var ok
          if(res.status === 200) ok = true
          else ok = false
          this.handleLogin(res.Ok, res.User, this.state.username, res.Manager, res.Admin, res.Superadmin, res.FullName)
        })
  }



  render() {
    return (
      <div className='login-page'>
        <fieldset>
          <legend>Prijava</legend>
          <div className='login-container'>
            <form onSubmit={this.handleSubmit}>
              <FormInput
                  name='username'
                  type='username'
                  handleChange={this.handleChange}
                  value={this.state.username}
                  label='Korisničko ime'
                  required
              />
              <FormInput
                  name='password'
                  type='password'
                  value={this.state.password}
                  handleChange={this.handleChange}
                  label='Lozinka'
                  required
              />
              <CustomButton type='submit'> Prijava </CustomButton>
              <p>{this.state.error}</p>
            </form>
          </div>
        </fieldset>
      </div>
    );
  }
}

export default AuthPage
